function decodeFrame(buffer) {
    /**
     * >>> 7 7
     * 10010030  ====>   00000001
     * &   11    
     * 1500001111  ,0
     * 11011000 & 00001111  ===  00001000
     *  
     */
    var fBite = buffer[0],
        /**
         * Fin
         * 1
         * 0  
         */
        Fin = fBite >>> 7,
        /**
         * opcodeopcodefBite4-7
         * &   11    
         * 1500001111  ,0
         */
        opcode = buffer[0] & 15,
        /**
         *  
         */
        len = buffer[1] & 127,
        // 1
        Mask = buffer[1] >>> 7,
        maskKey = null
    // 
    //1252
    if (len == 126) {
        len = buffer.readUInt16BE(2)
    } else if (len == 127) {
        // 655358
        len = buffer.readUInt64BE(2)
    }
    // 
    Mask && (maskKey = buffer.slice(2,5))
    /**
     *  
     * octetstexti%4(i4)
     */
    if(Mask){
        for (var i = 2;i<len ;i++){
            buffer[i] = maskKey[(i - 2) % 4] ^ buffer[i];
        }
    }
    var data = buffer.slice(2)
    return {
        Fin:Fin,
        opcode:opcode,
        data:data
    }
}
